const camps_to_parse = $input.all() || [];

const all_camps_data = $("All camps data").all() || [];


// --- ШАГ 1: Функции для обработки и сравнения строк ---

// Определяем набор стоп-слов, которые нужно игнорировать.
// Использование Set обеспечивает очень быстрый поиск (метод .has()).
const STOP_WORDS = new Set(['at', 'the', 'in', 'a', 'an', 'for', 'of', 'on']);

/**
 * Нормализует строку и превращает ее в Set уникальных, значащих слов.
 * @param {string} text - Исходная строка.
 * @returns {Set<string>} - Набор слов.
 */
function preprocessStringToSet(text) {
    if (!text || typeof text !== 'string') {
        return new Set();
    }
    // 1. Привести к нижнему регистру и удалить все символы, кроме букв, цифр и пробелов.
    // Флаг 'g' означает "глобальный" поиск, чтобы заменить все вхождения.
    const normalizedText = text.toLowerCase().replace(/[^\w\s]/g, '');

    // 2. Разбить на слова и отфильтровать пустые строки и стоп-слова.
    const tokens = normalizedText.split(/\s+/) // Разделяем по одному или нескольким пробелам
        .filter(word => word.length > 0 && !STOP_WORDS.has(word));

    // 3. Вернуть как Set для уникальности и быстрых операций.
    return new Set(tokens);
}

/**
 * Вычисляет коэффициент схожести Жаккара для двух наборов (Set).
 * @param {Set<string>} set1
 * @param {Set<string>} set2
 * @returns {number} - Коэффициент от 0 до 1.
 */
function jaccardSimilarity(set1, set2) {
    const intersection = new Set([...set1].filter(word => set2.has(word)));
    const union = new Set([...set1, ...set2]);

    // Защита от деления на ноль, если оба набора пустые.
    if (union.size === 0) {
        return 1.0; // Два пустых набора идентичны
    }

    return intersection.size / union.size;
}


// --- ШАГ 2: Основная логика слияния ---

// Используем метод .map(), чтобы преобразовать каждый элемент исходного массива в новый
const mergedCamps = camps_to_parse.map(campToFind => {
    let bestMatch = null;
    let highestScore = -1;

    const setToFind = preprocessStringToSet(campToFind.json.camp_name);

    for (const potentialMatch of all_camps_data) {
        const potentialSet = preprocessStringToSet(potentialMatch.json.Name.trim());
        const score = jaccardSimilarity(setToFind, potentialSet);

        if (score > highestScore) {
            highestScore = score;
            bestMatch = potentialMatch;
        }
    }

    // Создаем новый объект, копируя все свойства из campToFind,
    // и добавляем результат поиска.
    const result = {
      name: campToFind.json.camp_name
    };

    // Устанавливаем порог уверенности, например, 0.5 (50% общих слов)
    if (bestMatch && highestScore > 0.5) {
      result.slug = bestMatch.json.Slug;
      // result.matched_data = bestMatch;
      // result.match_score = highestScore;
    } else {
      result.slug = null;
      // result.matched_data = null;
      // result.match_score = 0;
    }

    return result;
});

return mergedCamps;

