/**
 * Вспомогательная функция для преобразования строки в набор уникальных слов (токенов).
 * @param {string} str - Исходная строка.
 * @returns {Set<string>} Набор уникальных токенов.
 */
function stringToTokenSet(str) {
    if (!str || typeof str !== 'string') {
        return new Set();
    }

    const tokens = str
        .toLowerCase()
        .replace(/[^\w\s-]/g, '')
        .split(/\s+/);

    return new Set(tokens.filter(Boolean)); 
}


/**
 * Анализирует массив строк на схожесть по методу Жаккара.
 * Сравнивает первую строку со всеми остальными и принимает решение об объединении.
 *
 * @param {string[]} stringArray - Массив строк для анализа.
 * @param {number} [threshold=0.5] - Порог схожести для принятия решения об объединении.
 * @returns {{decision: ('AGGREGATE'|'SEPARATE'), scores: number[]}} - Объект с решением и массивом коэффициентов.
 */
function analyzeJaccardSimilarity(stringArray, threshold = 0.2) {
    // Обработка крайних случаев: если в массиве меньше двух строк, сравнивать нечего.
    if (!Array.isArray(stringArray) || stringArray.length < 2) {
        return {
            decision: 'AGGREGATE', // Нечего объединять
            scores: []
        };
    }

    // Первая строка становится эталоном для сравнения
    const referenceSet = stringToTokenSet(stringArray[0]);
    const scores = [];

    // Сравниваем каждую последующую строку с эталоном
    for (let i = 1; i < stringArray.length; i++) {
        const currentSet = stringToTokenSet(stringArray[i]);

        // Логика расчета коэффициента Жаккара
        const intersection = new Set([...referenceSet].filter(token => currentSet.has(token)));
        const union = new Set([...referenceSet, ...currentSet]);

        const score = union.size === 0 ? 1.0 : intersection.size / union.size;
        scores.push(score);
    }

    // Принимаем решение: нужно ли объединять?
    // Объединяем, только если КАЖДАЯ строка похожа на первую (все коэф. > порога)
    const shouldAggregate = scores.every(score => score > threshold);
    const decision = shouldAggregate ? 'AGGREGATE' : 'SEPARATE';

    return {
        decision: decision,
        scores: scores
    };
}


