/**
 * Compare two values and return differences
 * @param {any} val1 - First value to compare
 * @param {any} val2 - Second value to compare
 * @param {string} path - Current path in the object
 * @returns {Array<{path: string, oldValue: any, newValue: any, type: string}>} Array of differences
 */
function compareValues(val1, val2, path) {
    // Handle different types
    if (typeof val1 !== typeof val2) {
        return [{
            path,
            oldValue: val2,
            newValue: val1,
            type: 'type_change'
        }];
    }

    // Handle arrays
    if (Array.isArray(val1) && Array.isArray(val2)) {
        if (val1.length !== val2.length) {
            return [{
                path,
                oldValue: val2,
                newValue: val1,
                type: 'array_length_change'
            }];
        }

        const differences = [];
        val1.forEach((item, index) => {
            const itemDiffs = compareValues(item, val2[index], `${path}[${index}]`);
            differences.push(...itemDiffs);
        });
        return differences;
    }

    // Handle objects
    if (typeof val1 === 'object' && val1 !== null && typeof val2 === 'object' && val2 !== null) {
        const differences = [];
        const allKeys = new Set([...Object.keys(val1), ...Object.keys(val2)]);

        for (const key of allKeys) {
            const newPath = path ? `${path}.${key}` : key;

            if (!(key in val1)) {
                differences.push({
                    path: newPath,
                    oldValue: val2[key],
                    newValue: undefined,
                    type: 'key_removed'
                });
            } else if (!(key in val2)) {
                differences.push({
                    path: newPath,
                    oldValue: undefined,
                    newValue: val1[key],
                    type: 'key_added'
                });
            } else {
                const valueDiffs = compareValues(val1[key], val2[key], newPath);
                differences.push(...valueDiffs);
            }
        }
        return differences;
    }

    // Handle primitive values
    if (val1 !== val2) {
        return [{
            path,
            oldValue: val2,
            newValue: val1,
            type: 'value_change'
        }];
    }

    return [];
}

/**
 * Compare n8n nodes and return differences
 * @param {Array} n8nNodes - Array of n8n nodes
 * @param {Array} gitNodes - Array of git nodes
 * @returns {Array} Array of differences
 */
function compareNodes(n8nNodes, gitNodes) {
    const differences = [];
    const n8nNodesMap = new Map(n8nNodes.map(node => [node.id, node]));
    const gitNodesMap = new Map(gitNodes.map(node => [node.id, node]));

    // Compare existing nodes
    n8nNodes.forEach((n8nNode, index) => {
        const gitNode = gitNodesMap.get(n8nNode.id);
        if (gitNode) {
            const changes = compareValues(n8nNode, gitNode, `[${index}]`);
            if (changes.length > 0) {
                differences.push({
                    type: n8nNode.type?.split('.')[1] || 'unknown',
                    name: n8nNode.name,
                    description: `Properties changed in ${n8nNode.name}`,
                    changes
                });
            }
        } else {
            differences.push({
                type: n8nNode.type?.split('.')[1] || 'unknown',
                name: n8nNode.name,
                description: `Node added: ${n8nNode.name}`,
                changes: [{
                    path: `[${index}]`,
                    oldValue: null,
                    newValue: n8nNode,
                    type: 'node_added'
                }]
            });
        }
    });

    // Check for deleted nodes
    gitNodes.forEach((gitNode, index) => {
        if (!n8nNodesMap.has(gitNode.id)) {
            differences.push({
                type: gitNode.type?.split('.')[1] || 'unknown',
                name: gitNode.name,
                description: `Node deleted: ${gitNode.name}`,
                changes: [{
                    path: `[${index}]`,
                    oldValue: gitNode,
                    newValue: null,
                    type: 'node_deleted'
                }]
            });
        }
    });

    return differences;
}

// Get the input data
const n8nWorkflowNodes = $input.first().json.n8nWorkflowJson;
const gitWorkflowNodes = $('Set Git Workflow Id').first().json.gitJsonWorkflowNodes;

// Add debug info
const items = [];

// Add debug information
items.push({
    json: {
        type: 'debug',
        description: 'Input data structure',
        n8nNodesCount: n8nWorkflowNodes.length,
        gitNodesCount: gitWorkflowNodes.length,
        n8nNodeIds: n8nWorkflowNodes.map(node => ({ id: node.id, name: node.name })),
        gitNodeIds: gitWorkflowNodes.map(node => ({ id: node.id, name: node.name }))
    }
});

// Compare the workflows
const differences = compareNodes(n8nWorkflowNodes, gitWorkflowNodes);

// Add the differences to the output
if (differences.length === 0) {
    items.push({
        json: {
            type: 'info',
            description: 'No changes detected',
            details: 'Deep comparison performed on all node properties'
        }
    });
} else {
    differences.forEach(diff => {
        items.push({
            json: {
                type: diff.type,
                name: diff.name,
                description: diff.description,
                changes: diff.changes
            }
        });
    });
}

// Return the items array for n8n
return items;
